import React from 'react';
import { useImagenContext } from '../context';

function ImagenCargada() {
  const { imagenCargada } = useImagenContext();

  return (
    <div className='imgcargada'>
      <h2>Imagen Cargada</h2>
      <div className='muestraimagen'>
        {imagenCargada && <img src={imagenCargada.url} alt='Imagen Cargada' />}
        {/* Muestra la imagen cargada de seleccionar imagen o capturar imagen */}  
      </div>
    </div>
  );
}

export default ImagenCargada;
