// CapturaImg.js
import React, { useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useImagenContext } from './context';

function CapturaImg() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const { setImagenCargada, setNombreImagen } = useImagenContext();

  useEffect(() => {
    // Detener la cámara cuando el componente se desmonta
    return () => {
      detenerCamara();
    };
  }, []);

  const iniciarCamara = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
    } catch (error) {
      console.error('Error al acceder a la cámara:', error);
    }
  };

  const detenerCamara = () => {
    const stream = videoRef.current?.srcObject;
    if (stream) {
      const tracks = stream.getTracks();
      tracks.forEach((track) => track.stop());
      videoRef.current.srcObject = null;
    }
  };

  const capturaImg = () => {
    const context = canvasRef.current.getContext('2d');
    const video = videoRef.current;

    canvasRef.current.width = video.videoWidth;
    canvasRef.current.height = video.videoHeight;

    context.drawImage(video, 0, 0, video.videoWidth, video.videoHeight);

    // Obtener la URL de datos de la imagen capturada
    const urlImagen = canvasRef.current.toDataURL('image/png');

    // Almacenar la imagen capturada en el contexto
    setImagenCargada({
      url: urlImagen,
      nombre: `imagen_capturada_${new Date().getTime()}.png`,
    });

    // Obtener el nombre de la imagen
    const nombre = `imagen_capturada_${new Date().getTime()}.png`;
    setNombreImagen(nombre);
  };

  return (
    <div className='tomarfoto'>
      <div className='botoncam'>
        <button onClick={iniciarCamara} className='botoncam'>
          <img src="busca.png" alt="Botón con imagen" />
          Iniciar cámara
        </button>
      </div>
      <div className='botoncam'>
        <button onClick={capturaImg} className='botoncam'>
          Tomar foto
        </button>
      </div>
      <div className='nombreimagencaptura'>
        <img src={imagenCargada.url} alt="Imagen capturada" />
        <p>Imagen: {imagenCargada.nombre}</p>
        <Link to="/resultado" className='botonbuscar'>Buscar</Link>
      </div>

      <video ref={videoRef} style={{ display: 'none' }} autoPlay />
      <canvas ref={canvasRef} style={{ display: 'none' }} />
    </div>
  );
}

export default CapturaImg;
