import './App.css';
import './contenedores.css';
import BarraNavegacion from './BarraNavegacion';
import Contenedor from './Contenedor';
import Resultado from './resutado/resultado';
import {BrowserRouter, Routes, Route} from 'react-router-dom';
import Seleccionarimg from './Seleccionimg';
import ImagenCargada from './resutado/ImagenCargada';

function App() {
  const [selectedImage, setSelectedImage] = useState(null);

  const handleImageSelect = (selectedFile) => {
    setSelectedImage(selectedFile);
  };


  return (
    <div>
      <BrowserRouter>
        <Routes>
          <Route
            path="/"
            element={<Seleccionarimg onImageSelect={handleImageSelect} />}
          />
          <Route
            path="/resultado"
            element={<ImagenCargada selectedImageInfo={selectedImage} />}
          />
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
