// Seleccionarimg.js
import React, { useRef } from 'react';
import { Link } from 'react-router-dom';
import { useImage } from './ImageContext';

function Seleccionarimg() {
  const inputRef = useRef(null);
  const { selectedImage, setSelectedImage } = useImage();

  const handleFileInputChange = (event) => {
    const selectedFile = event.target.files[0];

    if (selectedFile) {
      setSelectedImage(URL.createObjectURL(selectedFile));
    }
  };

  const handleButtonClick = () => {
    inputRef.current.click();
  };

  return (
    <div>
      <input
        type="file"
        accept="image/*"
        style={{ display: 'none' }}
        ref={inputRef}
        onChange={handleFileInputChange}
      />
      <div className='contenedorSuperior'>
        <button onClick={handleButtonClick} className='busqueda'>
          Seleccionar imagen:
        </button>
        <div className='contbusqueda'>
          {selectedImage && (
            <div className='lado'>
              <p>Nombre: {selectedImage.name}</p>
              <Link to="/resultado" className='botonbuscar'>
                Buscar
              </Link>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Seleccionarimg;
