import React, { useRef, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function Seleccionarimg({ onImageSelect }) {
  const inputRef = useRef(null);
  const [selectedImageName, setSelectedImageName] = useState('');
  const [mostrarBotonBuscar, setMostrarBotonBuscar] = useState(false);
  const navigate = useNavigate();

  const handleFileInputChange = (event) => {
    const selectedFile = event.target.files[0];

    if (selectedFile) {
      setSelectedImageName(selectedFile.name);
      setMostrarBotonBuscar(true);
      // Llamamos a la función del componente principal para pasar la información
      onImageSelect(selectedFile);
    }
  };

  const handleButtonClick = () => {
    inputRef.current.click();
  };

  const handleBuscarClick = () => {
    // Realizar la lógica de búsqueda
    // ...

    // Después de realizar la búsqueda, redirigir a /resultado
    navigate('/resultado');
  };

  return (
    <div>
      <input
        type="file"
        accept="image/*"
        style={{ display: 'none' }}
        ref={inputRef}
        onChange={handleFileInputChange}
      />
      <div className="contenedorSuperior">
        <button onClick={handleButtonClick} className="busqueda">
          Seleccionar imagen:
        </button>
        <div className="contbusqueda">
          {selectedImageName && (
            <div className="lado">
              <p>{selectedImageName}</p>
              {mostrarBotonBuscar && (
                <button onClick={handleBuscarClick} className="botonbuscar">
                  Buscar
                </button>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Seleccionarimg;
