import React, { useRef, useState } from 'react';

function Seleccionarimg() {
  const inputRef = useRef(null);
  const [selectedImageName, setSelectedImageName] = useState('');
  const [mostrarBotonBuscar, setMostrarBotonBuscar] = useState(false);

  const handleFileInputChange = (event) => {
    const selectedFile = event.target.files[0];

    if (selectedFile) {
      setSelectedImageName(selectedFile.name);
      setMostrarBotonBuscar(true);
    }
  };

  const handleButtonClick = () => {
    inputRef.current.click();
  };

  const handleBuscarClick = () => {
    alert('Realizando búsqueda...');
    console.log('Buscar haciendo clic en el botón');
  };

  return (
    <div>
      <input
        type="file"
        accept="image/*"
        style={{ display: 'none' }}
        ref={inputRef}
        onChange={handleFileInputChange}
      />
      <div className='Nombre'>
        <p>{selectedImageName}</p>
      </div>
      {mostrarBotonBuscar && (
        <button onClick={handleBuscarClick} className='botonbuscar'>
          Buscar
        </button>
      )}
      <button onClick={handleButtonClick} className='busqueda'>
        Seleccionar imagen
      </button>
    </div>
  );
}

export default Seleccionarimg;
