import React from 'react';
import { useLocation } from 'react-router-dom'; // Importa useLocation
import { useContextCaptura } from '../Contextcaptura';
function ImagenCargadaCaptura() {
  const location = useLocation(); // Usa el hook useLocation para obtener la informaci贸n de la ubicaci贸n
  const { imagenCapturada } = location.state || {}; // Accede a location.state

  const { imagenCargada } = useContextCaptura();

  console.log('imagenCargada:', imagenCargada);

  return (
    <div className='imgencontrada'>
      Imagen Cargada  
      {imagenCapturada && (
        <div className='muestraimagen'>
          <img
            src={imagenCapturada}
            alt="Imagen capturada"
            className='result'
            style={{ width: '200px', height: '300px' }}
          />
        </div>
      )}
      {/* Otro contenido de la pantalla de resultado */}
    </div>
  );
}

export default ImagenCargadaCaptura;
