import React, { useRef, useState } from 'react';

function Seleccionarimg() {
  const inputRef = useRef(null);
  const [selectedImageName, setSelectedImageName] = useState('');
  const [mostrarBotonBuscar, setMostrarBotonBuscar] = useState(false);

  const handleFileInputChange = (event) => {
    const selectedFile = event.target.files[0];

    if (selectedFile) {
      setSelectedImageName(selectedFile.name);
      setMostrarBotonBuscar(true);
    }
  };

  const handleButtonClick = () => {
    inputRef.current.click();
  };

  const handleBuscarClick = () => {
    // Lógica para la búsqueda
    // Puedes implementar lo que sea necesario al hacer clic en "Buscar"
    alert('Realizando búsqueda...'); // Muestra una alerta al hacer clic
  };

  return (
    <div>
      <input
        type="file"
        accept="image/*"
        style={{ display: 'none' }}
        ref={inputRef}
        onChange={handleFileInputChange}
      />
      <div className='contenedorSuperior'>
        <button onClick={handleButtonClick} className='busqueda'>
          Seleccionar imagen:
        </button>

          <div className='Nombre'>
          </div>
          {selectedImageName && (
            <div className='lado'>
              <p>{selectedImageName}</p>
              {mostrarBotonBuscar && (
                <button onClick={handleBuscarClick} className='botonbuscar'>Buscar</button>
              )}
            </div>
          )}

      </div>
    </div>
  );
}

export default Seleccionarimg;
