import React, { useRef, useState } from 'react';
import { Link } from 'react-router-dom';
import ImagenCargadaCaptura from './resutado/ImagenCargadaCaptura';
import { useContextCaptura } from './Contextcaptura';

function CapturaImg() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [imagenCapturada, setImagenCapturada] = useState(null);
  const [camaraIniciada, setCamaraIniciada] = useState(false);
  const [nombreImagen, setNombreImagen] = useState(null); // Agregada esta línea


  const alternarCamara = async () => {
    try {
      if (!camaraIniciada) {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        videoRef.current.srcObject = stream;
      } else {
        detenerCamara();
      }
      setCamaraIniciada(!camaraIniciada);
    } catch (error) {
      console.error('Error al acceder a la cámara:', error);
    }
  };

  const detenerCamara = () => {
    const stream = videoRef.current.srcObject;
    const tracks = stream.getTracks();

    tracks.forEach((track) => track.stop());
    videoRef.current.srcObject = null;
  };
  const CapturaImg = () => {
    const videoRef = useRef(null);
    const canvasRef = useRef(null);
    const { imagenCargada, setNombreImagen } = useContextCaptura();
    const [imagenCapturada, setImagenCapturada] = useState(null);
    const [camaraIniciada, setCamaraIniciada] = useState(false);

    const alternarCamara = async () => {
      try {
        if (!camaraIniciada) {
          const stream = await navigator.mediaDevices.getUserMedia({ video: true });
          videoRef.current.srcObject = stream;
        } else {
          detenerCamara();
        }
        setCamaraIniciada(!camaraIniciada);
      } catch (error) {
        console.error('Error al acceder a la cámara:', error);
      }
    };

    const detenerCamara = () => {
      const stream = videoRef.current.srcObject;
      const tracks = stream.getTracks();

      tracks.forEach((track) => track.stop());
      videoRef.current.srcObject = null;
    };

    const capturaImg = () => {
      const context = canvasRef.current.getContext('2d');
      const video = videoRef.current;

      canvasRef.current.width = video.videoWidth;
      canvasRef.current.height = video.videoHeight;

      context.drawImage(video, 0, 0, video.videoWidth, video.videoHeight);

      // Obtener la URL de datos de la imagen capturada
      const urlImagen = canvasRef.current.toDataURL('image/png');
      setImagenCapturada(urlImagen);

      // Obtener el nombre de la imagen
      const nombre = `imagen_capturada_${new Date().getTime()}.png`;

      // Corregir la declaración de nombreImagen
      setNombreImagen(nombre);

      // Detener la cámara después de capturar la imagen
      detenerCamara();
      setCamaraIniciada(false);
    };

    const descargarImagen = () => {
      const enlaceDescarga = document.createElement('a');
      enlaceDescarga.href = imagenCapturada;
      enlaceDescarga.download = nombreImagen || 'imagen_capturada.png';
      enlaceDescarga.click();
    };

    return (
      <div className='tomarfoto'>
        <div className='botoncam'>
          <button onClick={alternarCamara} className='botoncam'>
            <img src="busca.png" alt="Botón con imagen" />
            {camaraIniciada ? 'Detener cámara' : 'Iniciar cámara'}
          </button>
        </div>
        <div className='botoncam'>
          {camaraIniciada && (
            <button onClick={capturaImg} className='botoncam'>Tomar foto</button>
          )}
        </div>

        {imagenCapturada && (
          <div className='nombreimagencaptura'>
            <ImagenCargadaCaptura imagenCapturadaUrl={imagenCapturada} />
            <p>Imagen: {nombreImagen}</p>
            <button onClick={descargarImagen}>Descargar Imagen</button>
            <Link to="/resultadocaptura" className='botonbuscar'>Buscar</Link>
          </div>
        )}

        <video ref={videoRef} style={{ display: 'none' }} autoPlay />
        <canvas ref={canvasRef} style={{ display: 'none' }} />
      </div>
    );
  }
}
  export default CapturaImg;
