// En el archivo de contexto (context.js)
import React, { createContext, useContext, useState } from 'react';

const ImagenContext = createContext();

export const ImagenProvider = ({ children }) => {
  const [imagenCapturada, setImagenCapturada] = useState(null);

  return (
    <ImagenContext.Provider value={{ imagenCapturada, setImagenCapturada }}>
      {children}
    </ImagenContext.Provider>
  );
};

export const useImagenContext = () => {
  return useContext(ImagenContext);
};
