// ImagenCargadaCaptura.js
import React, { useContext } from 'react';
import './resultados.css';
import { useContextCaptura } from '../Contextcaptura';

function ImagenCargadaCaptura() {
  const { imagenCapturada, nombreImagen } = useContext(useContextCaptura); // Utiliza el nombre correcto del contexto


  return (
    <div className='imgcargada'>
      Imagen Cargada
      <div className='muestraimagen'>
        {imagenCargada && (
          <div>
            <img
              src={URL.createObjectURL(imagenCapturada)}
              alt={`Imagen Cargada`}
              className='result'
              style={{ width: '200px', height: '300px' }}
            />
          </div>
        )}
      </div>
    </div>
  );
}


export default ImagenCargadaCaptura;
