import React from 'react';
import { useImagenContext } from '../context';
import "../Seleccionimg";
import "../capturarimg";

function ImagenCargada() {
  const { imagenCargada } = useImagenContext();
  console.log('Imagen Cargada:', imagenCargada);

  return (
    <div className='imgcargada'>
      Imagen Cargada
      <div className='muestraimagen'>
        {imagenCargada.url && (
          <div>
            <p>URL de la imagen:</p>
            <a href={imagenCargada.url} target="_blank" rel="noopener noreferrer">
              {imagenCargada.url}
            </a>
            <img src={imagenCargada.url} alt={`Imagen Cargada: ${imagenCargada.nombre}`} />
          </div>
        )}
      </div>
    </div>
  );

