// Contenedor3.js

import React, { useState, useEffect } from 'react';

function Contenedor3() {
  const [imagenes, setImagenes] = useState([]);

  useEffect(() => {
    // Obtener la lista de imágenes de la carpeta "vestidos"
    const importarImagenes = (context) => context.keys().map(context);
    const imagenesContext = require.context('./home/ivanna/Documents/tt2/inicio/public/vestidos', false, /\.(png|jpe?g|svg)$/);
    const imagenesLista = importarImagenes(imagenesContext);

    // Seleccionar tres imágenes aleatorias
    const imagenesAleatorias = [];
    while (imagenesAleatorias.length < 3) {
      const indiceAleatorio = Math.floor(Math.random() * imagenesLista.length);
      if (!imagenesAleatorias.includes(indiceAleatorio)) {
        imagenesAleatorias.push(indiceAleatorio);
      }
    }

    // Establecer el estado con las imágenes seleccionadas
    setImagenes(imagenesAleatorias.map((indice) => imagenesLista[indice]));
  }, []); // El segundo argumento [] asegura que este efecto solo se ejecute una vez al montar el componente

  return (
    <div className="contenedor3">
      <p style={{ fontSize: '25px', fontFamily: 'Arial, sans-serif', margin: '10px', color: '#5e5e5e' }}>ROPA PARA DAMA</p>
      <div style={{ display: 'flex' }}>
        {imagenes.map((imagen, index) => (
          <img key={index} src={imagen} alt={`Imagen ${index}`} style={{ maxWidth: '100%', marginRight: '10px' }} />
        ))}
      </div>
    </div>
  );
}

export default Contenedor3;
