import React, { useRef, useState } from 'react';

function Seleccionarimg() {
  const inputRef = useRef(null);
  const [selectedImage, setSelectedImage] = useState(null);
  const [selectedImageName, setSelectedImageName] = useState('');

  const handleFileInputChange = (event) => {
    const selectedFile = event.target.files[0];

    if (selectedFile) {
      setSelectedImageName(selectedFile.name);

      const reader = new FileReader();
      reader.onload = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(selectedFile);
    }
  };

  const handleButtonClick = () => {
    inputRef.current.click();
  };

  return (
    <div>
      <input
        type="file"
        accept="image/*"
        style={{ display: 'none' }}
        ref={inputRef}
        onChange={handleFileInputChange}
      />
      <button onClick={handleButtonClick} className='busqueda'>Seleccionar imagen</button>

      {selectedImage && (
        <div>
          <img src={selectedImage} alt="Imagen seleccionada" style={{ maxWidth: '100%', maxHeight: '200px' }} />
          <p>Nombre de la imagen: {selectedImageName}</p>
        </div>
      )}
    </div>
  );
}

export default Seleccionarimg;
