import React, { createContext, useContext, useState } from 'react';

const ImagenContext = createContext();

export const useImagenContext = () => useContext(ImagenContext);

export const ImagenProvider = ({ children }) => {
  const [imagen, setImagen] = useState({
    imagenCargada: null,
    imagenCapturada: null,
    nombreImagenCargada: null,
    nombreImagenCapturada: null,
  });

  const setImagenCargada = (imagenCargada, nombreImagen) => {
    setImagen((prevImagen) => ({
      ...prevImagen,
      imagenCargada,
      nombreImagenCargada: nombreImagen,
    }));
  };

  const setImagenCapturada = (imagenCapturada, nombreImagen) => {
    setImagen((prevImagen) => ({
      ...prevImagen,
      imagenCapturada,
      nombreImagenCapturada: nombreImagen,
    }));
  };

  return (
    <ImagenContext.Provider value={{ imagen, setImagenCargada, setImagenCapturada }}>
      {children}
    </ImagenContext.Provider>
  );
};
