import React, { useRef, useState } from 'react';
import {Link} from "react-router-dom";
function Seleccionarimg() {
  const inputRef = useRef(null);
  const [selectedImageName, setSelectedImageName] = useState('');
  const [mostrarBotonBuscar, setMostrarBotonBuscar] = useState(false);function App() {
  const [selectedImage, setSelectedImage] = useState(null);

    const handleImageSelect = (selectedFile) => {
      setSelectedImage(selectedFile);
    };

    return (
      <div>
        <Seleccionarimg onImageSelect={handleImageSelect} />
        {selectedImage && <ImagenCargada selectedImageInfo={selectedImage} />}
      </div>
    );
  }


  const handleFileInputChange = (event) => {
    const selectedFile = event.target.files[0];

    if (selectedFile) {
      setSelectedImageName(selectedFile.name);
      setMostrarBotonBuscar(true);
    }
  };

  const handleButtonClick = () => {
    inputRef.current.click();
  };

  /*const handleBuscarClick = () => {
    <button onClick={handleBuscarClick}>
     <Link className="redireccion" to="/resultado"></Link>
    </button>
  };*/

  return (
    <div>
      <input
        type="file"
        accept="image/*"
        style={{ display: 'none' }}
        ref={inputRef}
        onChange={handleFileInputChange}
      />
      <div className='contenedorSuperior'>
        <button onClick={handleButtonClick} className='busqueda'>
          Seleccionar imagen:
        </button>
        <div className='contbusqueda'>
          {selectedImageName && (
            <div className='lado'>
              <p>{selectedImageName}</p>
              {mostrarBotonBuscar && (
                <Link to="/resultado" className='botonbuscar'>
                  Buscar
                </Link>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Seleccionarimg;
